//模型里使用
//使用init方法统一注册模型事件
class User extends Model{
    protected static function init(){
        User::event('before_insert',function($user){
            if($user->status != 1){
                return false;
            }
        }
        //注册第二个事件
        User::event('before_insert','beforeInsert');
    }
}

