//Thinkphp中mysql查询字段不为空is not null 的写法
$pic_banner['pic'] = array('exp','!= ""');
$banner = $News->field("pic")->where($pic_banner)->order('dateline desc')->limit(5)->select();

where("url is null or url=''");

$where = new Where;
$where['pid'] = 494;//应用中心 栏目ID
$where['deleted'] = 0;//状态 0正常 1回收站
$where['hide'] = 1;//显示是否 0显示 1隐藏
$rs=Db::name('column')
  ->field('id,icon,position,level,title,hide,url,controller,sort,pid,pid_all,spread,state,description')
